    req = new Hello_Type();
    req.setArgument("foo");
    req.setExtra("bar");

    helloPort.getRequestContext().put(BindingProvider.ENDPOINT_ADDRESS_PROPERTY, endpointAddress);

    response = stub.hello(req);
    assertNull(response);
} catch (Exception e) {
    rc = stub.getResponseContext();
    assertEquals(404,rc.get(MessageContext.HTTP_RESPONSE_CODE));
}

