createDispatchJAXB() {
    context = createJAXBContext();
    portQName = new QName("urn:test", "HelloPort");
    service.addPort(portQName,
      "http://schemas.xmlsoap.org/wsdl/soap/http",
      helloPortAddress);
    dispatch = service.createDispatch(portQName, context, Service.Mode.PAYLOAD);
    setTransport(dispatch);
    return dispatch;
}

descriptor.client:
dispatch = getDispatchJAXB();
try {
    req = new Hello_Type();
    req.setArgument("foo");
    req.setExtra("bar");

    dispatch.getRequestContext().put(BindingProvider.ENDPOINT_ADDRESS_PROPERTY, endpointAddress);
    assertNull(dispatch.invoke(req));
} catch (Exception e) {
    //e.printStackTrace();
    rc = dispatch.getResponseContext();
    assertEquals(404,rc.get(MessageContext.HTTP_RESPONSE_CODE));
}
